import React, { useState, useContext } from "react";
import { Context } from "../../../context";
import * as uuid from "uuid";
import "../../../styles/Popup.css";
import "../../../styles/Main.css";

function AddCardPopup(props) {
  const { dispatch } = useContext(Context);
  const [cardTitle, setCardTitle] = useState("");
  const [cardDescription, setCardDescription] = useState("");
  const createCard = (e) => {
    if (e) {
      e.preventDefault();
    }

    if (cardTitle.length && cardDescription.length) {
      dispatch({
        type: "ADD_CARD",
        payload: {
          board_id: props.boardId,
          card: {
            title: cardTitle,
            description: cardDescription,
            id: uuid.v4(),
            type: props.type,
            draggable: "true",
            className: "card",
          },
        },
      });

      setCardTitle("");
      props.setPopupValue();

      setCardDescription("");
      props.setPopupValue();
    }
  };


  const createCardByEnter = (e) => {
    if (e.key === "Enter" && cardTitle.length) {
      dispatch({
        type: "ADD_CARD",
        payload: {
          board_id: props.boardId,
          card: {
            title: cardTitle,
            id: uuid.v4(),
            type: props.type,
            draggable: "true",
            className: "card",
          },
        },
      });

      setCardTitle("");
      props.setPopupValue();
    }
  };

  const closePopupNoPropagation = (e) => {
    e.stopPropagation();
  };

  const closePopup = () => {
    props.setPopupValue();

  };
  return (
    <div className="popup-container" onClick={closePopup}>
      <div className="popup-content" onClick={closePopupNoPropagation}>
        <div className="create-wrapper">
          <h2>Agregar tarea:</h2>
          <input
            type="text"
            value={cardTitle}
            onKeyPress={createCardByEnter}
            onChange={(event) => setCardTitle(event.target.value)}
          />
          <input
            type="text"
            value={cardDescription}
            onKeyPress={createCardByEnter}
            onChange={(event) => setCardDescription(event.target.value)}
          />
          <div className="close-wrapper">
            <button onClick={closePopup}>Cerrar</button>
            <button onClick={createCard}>Aceptar</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddCardPopup;
