import React, { useReducer, useEffect } from 'react';
import BoardList from './components/BoardModule/BoardList';
import { Context } from './context';
import reducer from './reducer';
import BASE_STATE from './utils/BaseState';
import './styles/Main.css';
import Footer from './components/Footer/Footer';
import Logo from './images/logo-andreani.png';

function App() {

    const [state, dispatch] = useReducer(reducer, (JSON.parse(localStorage.getItem('boards')) || BASE_STATE));

    useEffect(() => {
        localStorage.setItem('boards', JSON.stringify(state));
    }, [state]);

    return (
        <Context.Provider value={{
            dispatch
        }}>
            <div className="App">
            <div className="logo">
        <img src={Logo}></img>
      </div>
                <h1 className="title-app">Tabla de tareas</h1>
                    <BoardList
                        boards={state}
                    ></BoardList>
                    <Footer/>
            </div>
        </Context.Provider>
    );
}

export default App;
