/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useContext } from "react";
import { Context } from "../../../context";
import * as uuid from "uuid";
import "../../../styles/Popup.css";
import "../../../styles/Main.css";
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function AddCardPopup(props) {
  const { dispatch } = useContext(Context);
  const [cardTitle, setCardTitle] = useState("");
  const [cardDescription, setCardDescription] = useState("");
  const createCard = (e) => {
    if (e) {
      e.preventDefault();
    }

    if (cardTitle.length && cardDescription.length) {
      dispatch({
        type: "ADD_CARD",
        payload: {
          board_id: props.boardId,
          card: {
            title: cardTitle,
            description: cardDescription,
            id: uuid.v4(),
            type: props.type,
            draggable: "true",
            className: "card",
          },
        },
      });

      setCardTitle("");
      props.setPopupValue();

      setCardDescription("");
      props.setPopupValue();
    }
  };


  const createCardByEnter = (e) => {
    if (e.key === "Enter" && cardTitle.length && cardDescription.length) {
      dispatch({
        type: "ADD_CARD",
        payload: {
          board_id: props.boardId,
          card: {
            title: cardTitle,
            description: cardDescription,
            id: uuid.v4(),
            type: props.type,
            draggable: "true",
            className: "card",
          },
        },
      });

      setCardTitle("");
      props.setPopupValue();

      setCardDescription("");
      props.setPopupValue();
    }
  };

  const closePopupNoPropagation = (e) => {
    e.stopPropagation();
  };

  const closePopup = () => {
    props.setPopupValue();

  };
  return (
      <div> <a href="#miModal">Abrir Modal</a>
<div id="miModal" class="modal">
  <div class="modal-contenido">
    <a href="#">X</a>
    <h2>Mi primer Modal</h2>
    <p>Este es mi primera ventana modal sin utilizar JavaScript.</p>
  </div>  
</div>
</div>

  );
}

export default AddCardPopup;
